<?php	// LECTURE	if( !in_array(USER_PAGE, array('maps', 'maps-order')) ){		$mapsDirectoryPath = AdminServ::getMapsDirectoryPath();		$mapsDirectoryList = AdminServUI::getMapsDirectoryList($mapsDirectoryPath, $directory);	}		// ACTIONS	if( isset($_POST['newFolderValid']) && $_POST['newFolderName'] != null || isset($_POST['newFolderName']) ){		if( Folder::create($mapsDirectoryPath.$directory.Str::replaceChars($_POST['newFolderName'])) !== true ){			AdminServ::error('Impossible de créer le dossier : '.$_POST['newFolderName']);		}		else{			if($directory){				Utils::redirection(false, '?p='. $_GET['goto'] .'&d='.$directory);			}			else{				Utils::redirection(false, '?p='. $_GET['goto']);			}		}	}			// MENU	$menuList = array(		'Liste' => 'maps',		'Local' => 'maps-local',		'Envoyer' => 'maps-upload',		'Ordonner' => 'maps-order',		'MatchSettings' => 'maps-matchset',	);?>