<?php	// LECTURE	if( in_array(USER_PAGE, array('maps-local', 'maps-upload', 'maps-matchset')) ){		$mapsDirectoryPath = AdminServ::getMapsDirectoryPath();		$mapsDirectoryList = AdminServUI::getMapsDirectoryList($mapsDirectoryPath, $directory);	}		// ACTIONS	$hasDirectory = null;	if($directory){		$hasDirectory = '&d='.$directory;	}	if( isset($_POST['newFolderValid']) && $_POST['newFolderName'] != null || isset($_POST['newFolderName']) ){		if( Folder::create($mapsDirectoryPath.$directory.Str::replaceChars($_POST['newFolderName'])) !== true ){			AdminServ::error('Impossible de créer le dossier : '.$_POST['newFolderName']);		}		else{			Utils::redirection(false, '?p='. $_GET['goto'].$hasDirectory);		}	}?>