<?php	// LECTURE	if( USER_PAGE != 'maps' && USER_PAGE != 'maps-order' ){		$mapsDirectoryPath = AdminServ::getMapsDirectoryPath();		$mapsDirectoryList = AdminServTemplate::getMapsDirectoryList($mapsDirectoryPath, $directory);	}		// ACTIONS	if( isset($_POST['newFolderValid']) && $_POST['newFolderName'] != null || isset($_POST['newFolderName']) ){		if( Folder::create($mapsDirectoryPath.$directory.Str::replaceChars($_POST['newFolderName'])) !== true ){			AdminServ::error('Impossible de crÃ©er le dossier : '.$_POST['newFolderName']);		}		else{			if($directory){				Utils::redirection(false, '?p='. $_GET['goto'] .'&d='.$directory);			}			else{				Utils::redirection(false, '?p='. $_GET['goto']);			}		}	}			// MENU	$mapsMenu = null;	$menuList = array(		'Liste' => 'maps',		'Envoyer' => 'maps-upload',		'Local' => 'maps-local',		'MatchSettings' => 'maps-matchset',		'Ordonner' => 'maps-order'	);		// Affichage du menu	if( count($menuList) > 0 ){		$mapsMenu = '<ul>';			foreach($menuList as $title => $page){				$mapsMenu .= '<li><a '; if(USER_PAGE == $page){ $mapsMenu .= 'class="active" '; } $mapsMenu .= 'href="?p='.$page; if($directory){ $mapsMenu .= '&amp;d='.$directory; } $mapsMenu .= '">'.$title.'</a></li>';			}		$mapsMenu .= '</ul>';	}?>